import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [textInput, setTextInput] = useState(""); // To store the text input
  const [loading, setLoading] = useState(false); // To handle the loading state
  const [videoUrl, setVideoUrl] = useState(null); // To display the uploaded video link

  // Handle text input change
  const handleTextChange = (e) => {
    setTextInput(e.target.value);
  };

  // Upload button click handler
  const handleUploadClick = async () => {
    if (!textInput) {
      alert("Please enter some text!");
      return;
    }

    // Show loading state while the video is being generated/uploaded
    setLoading(true);

    try {
      // Send a request to backend to login to YouTube and upload the video
      const params = new URLSearchParams({
        videoUrl: `https://example.com/generated-video.mp4?prompt=${encodeURIComponent(textInput)}`,
        title: "AI Generated Video",
        description: `Generated from: ${textInput}`,
        redirect: "http://localhost:3000/uploaded" // Redirect back to this URL once upload is complete
      });

      window.location.href = `http://localhost:5000/api/login?${params.toString()}`;
    } catch (err) {
      console.error("Error during upload:", err);
      setLoading(false);
      alert("An error occurred during the upload.");
    }
  };

  return (
    <div>
      <h1>AI Video Generator</h1>

      {/* Text input for user to enter prompt */}
      <textarea
        value={textInput}
        onChange={handleTextChange}
        placeholder="Enter your video prompt here..."
        rows="4"
        cols="50"
      />

      <div>
        {/* Upload Button */}
        <button onClick={handleUploadClick} disabled={loading}>
          {loading ? "Uploading..." : "Upload to YouTube"}
        </button>
      </div>

      {/* Display the video link once uploaded */}
      {videoUrl && (
        <div>
          <h2>Your video is live!</h2>
          <a href={videoUrl} target="_blank" rel="noreferrer">
            Watch your video on YouTube
          </a>
        </div>
      )}
    </div>
  );
}
