import React, { useState } from "react";
import axios from "axios";

function App() {
    const [step, setStep] = useState("start");
    const [prompt, setPrompt] = useState("");
    const [videoUrl, setVideoUrl] = useState("");
    const [youtubeUrl, setYoutubeUrl] = useState("");
    const [instagramUrl, setInstagramUrl] = useState("");

    const generateVideo = async () => {
        const response = await axios.post("http://localhost:5050/generate-video", { prompt });
        setVideoUrl(response.data.videoUrl);
    };

    const uploadToYouTube = async () => {
        const response = await axios.post("http://localhost:5000/upload-youtube", { videoUrl });
        setYoutubeUrl(response.data.youtubeUrl);
    };

    const uploadToInstagram = async () => {
        const response = await axios.post("http://localhost:5000/upload-instagram", { videoUrl });
        setInstagramUrl(response.data.instagramUrl);
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            {step === "start" ? (
                <button onClick={() => setStep("upload")}>Create Video</button>
            ) : (
                <>
                    <input type="text" value={prompt} onChange={(e) => setPrompt(e.target.value)} placeholder="Enter video prompt" />
                    <button onClick={generateVideo}>Generate Video</button>

                    {videoUrl && (
                        <div>
                            <button onClick={uploadToYouTube}>Upload to YouTube</button>
                            <button onClick={uploadToInstagram}>Upload to Instagram</button>
                        </div>
                    )}

                    {youtubeUrl && <p><a href={youtubeUrl} target="_blank">Watch on YouTube</a></p>}
                    {instagramUrl && <p><a href={instagramUrl} target="_blank">Watch on Instagram</a></p>}
                </>
            )}
        </div>
    );
}

export default App;
