import React, { useState } from 'react';

function App() {
  const [prompt, setPrompt] = useState(''); // Store text from the textbox
  const [selectedFile, setSelectedFile] = useState(null); // Store selected MP4 file
  const [videoUrl, setVideoUrl] = useState(null); // Store the video URL after upload (if any)

  // Handle MP4 file selection
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'video/mp4') {
      setSelectedFile(file);
      console.log('Selected MP4 file:', file);
    } else {
      alert('Please select a valid MP4 file.');
    }
  };

  // Handle video upload to server
  const handleUpload = () => {
    if (!selectedFile) {
      alert('No file selected!');
      return;
    }

    const formData = new FormData();
    formData.append('video', selectedFile);

    console.log(fetch('http://localhost:3000/upload', {
        method: 'POST',
        body: formData,
      }))

    const response = axios.post('https://api.runwayml.com/v1/video/generate')

    fetch('http://localhost:3000/upload', {
      method: 'POST',
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => {
        console.log('Upload response:', data);
        setVideoUrl(data.videoUrl); // Save the video URL if returned
        alert('Video uploaded successfully!');
      })
      .catch((err) => {
        console.error('Upload failed:', err);
        alert('Video upload failed.');
      });
  };

  // Handle video upload to YouTube
  const handleUploadToYouTube = () => {
    if (!videoUrl) {
      alert('Please upload a video first!');
      return;
    }

    // Redirect to YouTube login
    //window.location.assign(`http://localhost:3000/api/login?videoUrl=${encodeURIComponent(videoUrl)}`);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Upload Video and Upload to YouTube</h2>

      <div>
        <label>Enter your prompt:</label>
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt"
          style={{ padding: '8px', width: '300px', marginBottom: '10px' }}
        />
      </div>

      <div style={{ marginTop: '20px' }}>
        <input
          type="file"
          accept="video/mp4"
          onChange={handleFileChange}
          style={{ marginBottom: '10px' }}
        />
        <br />
        <button onClick={handleUpload}>Upload MP4</button>
      </div>

      <div style={{ marginTop: '20px' }}>
        <button onClick={handleUploadToYouTube} disabled={!videoUrl}>
          Upload to YouTube
        </button>
      </div>

      {videoUrl && (
        <div style={{ marginTop: '20px' }}>
          <h3>Uploaded Video URL:</h3>
          <a href={videoUrl} target="_blank" rel="noopener noreferrer">
            {videoUrl}
          </a>
        </div>
      )}
    </div>
  );
}

export default App;
