import React, { useState } from "react";
import axios from "axios";

export default function App() {
  const [textInput, setTextInput] = useState(""); // Text input from user
  const [videoUrl, setVideoUrl] = useState(null); // To store generated video URL
  const [loading, setLoading] = useState(false); // Loading state for generating video
  const [uploading, setUploading] = useState(false); // Loading state for uploading to YouTube

  // Handle text input change
  const handleTextChange = (e) => {
    setTextInput(e.target.value);
  };

  // Handle Generate Video button click
  const handleGenerateClick = async () => {
    if (!textInput) {
      alert("Please enter some text to generate a video!");
      return;
    }

    setLoading(true);

    try {
      // Send text input to backend to generate the video
      const response = await axios.post("http://localhost:5050/generate-video", { prompt: textInput });
      console.log(response)
      // Store the video URL received from backend
      setVideoUrl(response.data.videoUrl);
      setLoading(false);
    } catch (err) {
      console.error("Error generating video:", err);
      setLoading(false);
      alert("An error occurred while generating the video.");
    }
  };

  // Handle Upload to YouTube button click
  const handleUploadClick = async () => {
    if (!videoUrl) {
      alert("Please generate a video first!");
      return;
    }

    setUploading(true);

    try {
      // Send a request to backend to log in and upload the video
      const params = new URLSearchParams({
        videoUrl: videoUrl,
        title: "AI Generated Video",
        description: `Generated from: ${textInput}`,
        redirect: "http://localhost:3000" // Redirect back to this URL once upload is complete
      });

      // Redirect to backend for login and upload
      window.location.href = `http://localhost:5050/api/login?${params.toString()}`;

    } catch (err) {
      console.error("Error uploading video to YouTube:", err);
      setUploading(false);
      alert("An error occurred while uploading the video to YouTube.");
    }
  };

  return (
    <div>
      <h1>AI Video Generator</h1>

      {/* Text input for user to enter prompt */}
      <textarea
        value={textInput}
        onChange={handleTextChange}
        placeholder="Enter your video prompt here..."
        rows="4"
        cols="50"
      />

      <div>
        {/* Generate Video Button */}
        <button onClick={handleGenerateClick} disabled={loading}>
          {loading ? "Generating..." : "Generate Video"}
        </button>

        {/* Upload to YouTube Button */}
        <button onClick={handleUploadClick} disabled={uploading || !videoUrl}>
          {uploading ? "Uploading..." : "Upload to YouTube"}
        </button>
      </div>

      {/* Display the video link once generated */}
      {videoUrl && (
        <div>
          <h2>Video Generated!</h2>
          <p>Your video is ready: <a href={videoUrl} target="_blank" rel="noreferrer">{videoUrl}</a></p>
        </div>
      )}
    </div>
  );
}
