import React, { useState } from 'react';
import axios from "axios";
import './App.css';

const { fal } = require("@fal-ai/client");

const FAL_KEY= "946cb8b7-5b35-433f-89ac-53d49f9b0c84:2d19cba2c3d08529cc2c9599658390b3"

fal.config({
    credentials: FAL_KEY, // Replace with your key
  });

function App() {
  const [prompt, setPrompt] = useState(''); // Store text from the textbox
  const [vid, setVid] = useState(null); // Store selected MP4 file
  const [videoUrl, setVideoUrl] = useState(null); // Store the video URL after upload (if any)

  const  generateVideo = async () => {

    const formData = new FormData();
    formData.append('prompt', prompt);

    try {

    const { request_id } = await fal.queue.submit("fal-ai/wan-t2v", {
        input: {
          prompt: prompt, // from user input
        },
      });

      let videoUrl = null;
      while (true) {
        const status = await fal.queue.status("fal-ai/wan-t2v", {
          requestId: request_id,
          logs: true,
        });

        if (status.status === "completed") {
          videoUrl = status?.output?.video?.url;
          break;
        } else if (status.status === "failed") {
          alert("Video generation failed.");
          return;
        }

        // Wait a bit before checking again
        await new Promise((r) => setTimeout(r, 2000));
      }

      const videoRes = await fetch(videoUrl);
        const blob = await videoRes.blob();
        const file = new File([blob], 'generated-video.mp4', { type: 'video/mp4' });

        // Save this video in your component state
        setVid(file);
        const url = URL.createObjectURL(blob);

        // Trigger download
        const a = document.createElement("a");
        a.href = url;
        a.download = `generated-video-${Date.now()}.mp4`;
        document.body.appendChild(a);
        a.click();
        a.remove();

        alert("Video downloaded!");
    } catch (err) {
        console.error("Error generating video:", err);
        alert("Something went wrong.");
    }

  }

  // Handle video upload to server
  const handleUpload = (platform) => {
    // if (!vid) {
    //   alert('No video generated!');
    //   return;
    // }

    platform = platform.toLowerCase()
    const formData = new FormData();
    formData.append('video', vid);

    axios.post(`http://localhost:5050/upload-${platform}`, formData)
    .then(response => {
        console.log(response.data)
    })

  };

  return (
    <div className="app-container">
      <h2>AI Video Publisher</h2>

      <input
        type="text"
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        placeholder="Enter your prompt..."
        className="prompt-input"
      />

      <button className="generate-button" onClick={generateVideo}>
        🎬 Generate Video
      </button>

      <div className="button-group">
        <button className="icon-button" onClick={() => handleUpload('youtube')}>
          <img src="https://cdn-icons-png.flaticon.com/512/1384/1384060.png" alt="YouTube" />
        </button>
        <button className="icon-button" onClick={() => handleUpload('instagram')}>
          <img src="https://cdn-icons-png.flaticon.com/512/1384/1384063.png" alt="Instagram" />
        </button>
        <button className="icon-button" onClick={() => handleUpload('reddit')}>
          <img src="https://cdn-icons-png.flaticon.com/512/2111/2111589.png" alt="Reddit" />
        </button>
        <button className="icon-button" onClick={() => handleUpload('tiktok')}>
          <img src="https://cdn-icons-png.flaticon.com/512/3046/3046122.png" alt="TikTok" />
        </button>
        <button className="icon-button" onClick={() => handleUpload('linkedin')}>
          <img src="https://cdn-icons-png.flaticon.com/512/1384/1384062.png" alt="LinkedIn" />
        </button>
      </div>
    </div>
  );
}

export default App;
